### SJS 10/22/14 ###
### Python script to generate data for plotting figure "domains_naive_struc.pdf".
### This (proudly ugly!) script will take FASTA files of the structural and naive alignment subsets and create text files to plot in R.
### Note that the sequences in the FASTA files used here were manually selected from full alignments to achieve a representative subset.
### The subsequent R code used for plotting is in the script "plot_domains.R".

import sys
import numpy as np
from Bio import AlignIO
numseq = 130 
type = sys.argv[1] #struc or naive
if type == "struc":
    cons = ""
    start = 2150
    stop = 3450
elif type == "naive":
    cons = ""
    start = 2300
    stop = 4800
else:
    raise AssertionError("\n\nSpecify argument struc or naive.")
aln = AlignIO.read(type + "_domain_alignment_subset.fasta", "fasta")


# determine which columns not to keep since all gaps
convert = {'-':0, 'A':1, 'O':2, 'I':3, 'M':4}
convert_back = {0: '-', 1:'A', 2:'O', 3:'I', 4:'M'}
length = stop - start
struc_aln = np.zeros( [len(aln), length], dtype = 'int8')
i=0
for record in aln:
    seq = str(record.seq[start:stop])
    newseq = np.zeros(length, dtype = 'int8')
    j=0
    for entry in seq:
        newseq[j] = convert[entry]
        j += 1
    struc_aln[i] = newseq
    i += 1



new_new = ['' for i in range(numseq)]
keep = []
y=0
for col in struc_aln.T:
    if not np.all(col == 0):
        keep.append(y + start)
        x = 0
        for row in col:
            new_new[x] += convert_back[row]         
            x += 1
    y+=1
new_cons = ''
for c in range(start,stop+1):
    if c in keep:
        new_cons += cons[c]
assert(len(new_cons) == len(new_new[0]))


outf = open(type + "_plotaln.txt", 'w')
outf.write("x\ty\tdomain\n")
count = 0

for entry in new_new:  
    used = 0
    for i in range(len(entry)):
        outf.write(str(used) + '\t' + str(count) + '\t' + entry[i] + '\n')
        used += 1
    count += 1
outf.close()

conf = open(type + "_plotcons.txt", "w")
conf.write("x\ty\tdomain\n")
count = 0
for i in range(len(new_cons)):
    conf.write(str(count) + '\t0\t' + new_cons[i] + '\n')
    count += 1
conf.close()
